(6, 1, 'category_hidden_newfile', 1230768000, '<i>{$phrase_hidden}</i>', 'Blank new file template for categories with hidden and/or locked information.', '', '', 'Alex'),
(7, 1, 'category_login', 1230768000, '<table align="center" cellspacing="0">\r\n  <tr>\r\n    <td>\r\n      <form method="post" action="user.php?node=login&type=category&id={$id}&referer={$referer}">\r\n      <table align="center" class="table" cellspacing="1" width="500">\r\n        <tr>\r\n          <td class="header"><strong>{$phrase_title_private_category}</strong></td>\r\n        </tr>\r\n        <tr>\r\n          <td class="subheader"><strong>{$phrase_password}</strong></td>\r\n        </tr>\r\n        <tr>\r\n          <td class="row"><input type="password" name="category_password" style="width: 99%;"></td>\r\n        </tr>\r\n      </table>\r\n      \r\n      <center>\r\n      <div id="buttons" style="width: 500px;"><input type="submit" class="button" name="login" value="{$phrase_verify}"></div>\r\n      </center>\r\n      </form>\r\n    </td>\r\n  </tr>\r\n</table>', 'Category login box for password protected categories.', '', '', 'Alex'),
