CREATE TABLE IF NOT EXISTS `phcdl_templates_email` (
  `template_id` int(10) NOT NULL AUTO_INCREMENT,
  `template_type` enum('0','1') NOT NULL DEFAULT '0',
  `template_name` text,
  `template_subject` text,
  `template_data` longtext,
  `template_data_bak` mediumtext,
  `template_original` mediumtext,
  `template_timestamp` int(10) unsigned NOT NULL DEFAULT '0',
