INSERT INTO `phcdl_templates_email` (`template_id`, `template_type`, `template_name`, `template_subject`, `template_data`, `template_data_bak`, `template_original`, `template_timestamp`, `template_author`) VALUES
(1, '0', 'file_broken_url_notification', 'File: Broken URL - {$file_name}', 'Hello,\r\n\r\n<b>{$file_name}</b>, has been reported as a broken URL or missing file when a user; <b>{$user_name}</b>, attempted to download the file, on {$archive_title}.\r\n\r\n{$archive_title} Team.', '', '', 1230768000, 'Alex'),
(2, '0', 'file_modified_url_notification', 'File: Data Mismatch - {$file_name}', 'Hello,\r\n\r\n<b>{$file_name}</b>, has been reported because the file size on the linked file does not match the database information when then user; <b>{$user_name}</b>, initiated the download of this file.\r\n\r\n<b>Has the file been modified?</b>\r\nEvery time a file is changed and uploaded to your archive, the file hash on the actual file will also be changed. You will need to update the database information aswell so that the hashes match. If you knew about the change to this file, simply checking the ''Synchronise File Information'' and or ''Synchronise All Mirror Information'' option on the file in question could resolve this issue.\r\nIf you were unaware of any changes, it is recommended that you investigate further to make sure your files have not been maliciously changed in any way.\r\n\r\n{$archive_title} Team.', '', '', 1230768000, 'Alex'),
(3, '0', 'file_submission_notification', 'New File Submission - {$file_name}', 'Hello,\r\n\r\n<b>{$user_name}</b>, has just submitted a new file to {$archive_title}.\r\n\r\n<b>File:</b>\r\n{$file_name}\r\n\r\n<b>Category:</b>\r\n{$file_category}\r\n\r\n<b>Version:</b>\r\n{$file_version}\r\n\r\n<b>Developer:</b>\r\n{$file_author}\r\n\r\n<b>Description:</b>\r\n{$file_description}\r\n\r\nThanks,\r\n{$archive_title} Team.', '', '', 1230768000, 'Alex'),
(4, '0', 'subscription_admin_notification', 'Transaction: {$transaction_api_id} [{$api_name}] - {$transaction_state}', 'Hello,\r\n\r\n<b>{$user_name}</b>, has recently ordered a subscription to <b>{$subscription_name}</b> on {$archive_title}. Login to the control panel to view more details on this transaction.\r\n\r\n<b>Transaction ID:</b>\r\n{$transaction_api_id}\r\n\r\n<b>Transaction State:</b>\r\n{$transaction_state}\r\n\r\n{$archive_title} Team.', '', '', 1230768000, 'Alex'),
